mapper.select:
select count(userid)
from hh_member
where userid = #{id}

mapper.update:
update hh_member
set userpwd=#{userPwd}, username=#{userName}, useraddress=#{userAddress}, userphone=#{userPhone}
where userid=#{userId}

mapper.select:
select userid, username
from hh_member
where userid = #{userid} and userpwd = #{userpwd}
